package one.block.arisenjava.error.utilities;

import org.jetbrains.annotations.NotNull;

/**
 * Error class is used when there is an exception while attempting to convert a
 * signature to Rix format and the signature is not canonical.
 * <br>
 *     * This exception only happens with signatures signed by a key generated by the SECP256K1
 *     algorithm.
 * <br>
 *     * The signature must be recreated and tested to pass this exception.
 */
public class RixFormatterSignatureIsNotCanonicalError extends RixFormatterError{
    public RixFormatterSignatureIsNotCanonicalError() {
    }

    public RixFormatterSignatureIsNotCanonicalError(@NotNull String message) {
        super(message);
    }

    public RixFormatterSignatureIsNotCanonicalError(@NotNull String message, @NotNull Exception exception) {
        super(message, exception);
    }

    public RixFormatterSignatureIsNotCanonicalError(@NotNull Exception exception) {
        super(exception);
    }
}
